import React from 'react'

export default class Tile extends React.Component{
  constructor(){
    super()
    this.state = {
      active: false
    }
  }

  render(){

    return(

           <div className='container-tile'>
              <div 
              className="tile" 
              onClick={() => this.setState({active: !this.setState.active})}
              style={{
                backgroundColor: this.serState.active ? 'green' : 'red',
                width: '100px',
                height: '100px'
              }}
              >
              </div>
           </div>
    )

  }
}
